// #    Program Purpose: Creates an object with the same keys as the provided object and values          #
// #                     generated by running the provided function for each value.                      #
// #     Program Author: Happi Yvan <ivensteinpoker@gmail.com>                                           #
// #       Program Date: February 15, 2020.                                                              #
// #                                                                                                     #
// #######################################################################################################

const processObj = (someObj = {}, func = f=>f) =>
    Object.keys(someObj).map((val) => func(someObj[val]))
            .reduce((acc, val, i) =>
                ({
                    ...acc,
                    [Object.keys(someObj)[i]]: val
                }),{});

(() => {

    const objA = {a: 1, b: 2, c: 3};
    const objB = {firstName: 'Peter', lastName: 'James'};

    console.log(processObj(objA, (val) => val + 1));
    console.log(processObj(objB, (val) => String(val).toUpperCase()));

})();